SELECT a.accident_history_idx AS accident_history_idx, d.driver_name AS accident_history_driver_name, a.accident_history_accident_date AS accident_history_accident_date, a.accident_history_description AS accident_history_description, a.accident_history_location AS accident_history_location, a.accident_history_is_at_fault AS accident_history_is_at_fault, t.taxi_license_plate AS accident_history_taxi_license_plate FROM accident_history a JOIN driver d ON d.driver_idx = a.accident_history_driver_id JOIN taxi t ON a.accident_history_taxi_idx = t.taxi_idx
a.accident_history_taxi_idx = #{searchIdx}
a.accident_history_driver_id = #{searchIdx}
AND a.accident_history_location LIKE CONCAT('%', #{searchText}, '%')
AND a.accident_history_description LIKE CONCAT('%', #{searchText}, '%')
AND t.taxi_license_plate LIKE CONCAT('%', #{searchText}, '%')
AND a.accident_history_is_at_fault = #{filterIsDriverFault}
AND a.accident_history_accident_date BETWEEN #{filterStartDate} AND #{filterEndDate}
ORDER BY a.accident_history_accident_date
ORDER BY a.accident_history_location
ORDER BY a.accident_history_description
ORDER BY d.driver_name
ORDER BY a.accident_history_accident_date
DESC
ASC
LIMIT #{pageSize} OFFSET #{page}

