mapper.select:
SELECT appr_midapprover, appr_finalapprover
FROM tb_approval_doc_line_bkmk
WHERE appr_line_bkmk_idx = #{lineId}

mapper.update:
UPDATE tb_doc_item_file 
SET appr_file_path = #{appr_file_path}, appr_file_dt = CURRENT_TIMESTAMP 
WHERE appr_file_name = #{appr_file_name}

mapper.select:
SELECT COUNT(*) 
FROM tb_doc_item_file 
WHERE appr_file_name = #{fileName}

SELECT appr_file_path 
FROM tb_doc_item_file 
WHERE appr_file_name = #{fileName}

SELECT ad.*, a.appr_midapprover, a.appr_finalapprover FROM tb_approval_doc ad JOIN tb_approval a ON ad.approval_doc_idx = a.approval_doc_idx WHERE (ad.approval_doc_id = #{loginId} OR a.appr_midapprover = ( SELECT CONCAT(e.emp_name, ' ', t.title_name) FROM employee e JOIN title t ON e.title_no = t.title_no WHERE e.emp_no = #{loginId} ) OR a.appr_finalapprover = ( SELECT CONCAT(e.emp_name, ' ', t.title_name) FROM employee e JOIN title t ON e.title_no = t.title_no WHERE e.emp_no = #{loginId} )) AND (ad.approval_doc_title LIKE CONCAT('%', #{query}, '%') OR ad.approval_doc_id LIKE CONCAT('%', #{query}, '%') OR a.appr_midapprover LIKE CONCAT('%', #{query}, '%') OR a.appr_finalapprover LIKE CONCAT('%', #{query}, '%') OR ad.approval_doc_write_date LIKE CONCAT('%', #{query}, '%'))
AND ad.approval_doc__state = #{status}
ORDER BY ad.approval_doc_write_date DESC LIMIT #{start}, #{pageSize}

SELECT emp_name
FROM employee
WHERE emp_no = #{loginId}

mapper.update:
UPDATE tb_approval_doc
SET approval_doc__state = #{approvalState},
    approval_doc_udt_dt = #{approvalDate}
WHERE approval_doc_idx = #{approvalDocIdx}

mapper.select:
SELECT d.approval_doc_idx, d.approval_doc_write_date, d.approval_doc_title, d.approval_doc_id, a.appr_midapprover, a.appr_finalapprover, a.appr_mngr_updt, d.approval_doc__state, d.approval_doc_path FROM tb_approval a JOIN tb_approval_doc d ON a.approval_doc_idx = d.approval_doc_idx WHERE (d.approval_doc_title LIKE CONCAT('%', #{query}, '%') OR d.approval_doc_id LIKE CONCAT('%', #{query}, '%') OR a.appr_midapprover LIKE CONCAT('%', #{query}, '%') OR a.appr_finalapprover LIKE CONCAT('%', #{query}, '%') OR d.approval_doc_write_date LIKE CONCAT('%', #{query}, '%'))
AND d.approval_doc__state = #{status}
ORDER BY d.approval_doc_write_date DESC LIMIT #{start}, #{pageSize}

SELECT ad.*, a.appr_midapprover, a.appr_finalapprover FROM tb_approval_doc ad JOIN tb_approval a ON ad.approval_doc_idx = a.approval_doc_idx WHERE (ad.approval_doc_title LIKE CONCAT('%', #{query}, '%') OR ad.approval_doc_id LIKE CONCAT('%', #{query}, '%') OR a.appr_midapprover LIKE CONCAT('%', #{query}, '%') OR a.appr_finalapprover LIKE CONCAT('%', #{query}, '%') OR ad.approval_doc_write_date LIKE CONCAT('%', #{query}, '%'))
AND ad.approval_doc__state = #{status}
ORDER BY ad.approval_doc_write_date DESC LIMIT #{start}, #{pageSize}

mapper.delete:
DELETE a FROM tb_approval a
JOIN tb_approval_doc d ON a.approval_doc_idx = d.approval_doc_idx
WHERE d.approval_doc_path = #{doc}
