mapper.select:
select count(emp_email)
from employee
where emp_email = #{param1}

SELECT COALESCE(MAX(SUBSTRING(emp_no, 2)), 0) AS latestEmpNo
FROM employee
WHERE SUBSTRING(emp_no, 1, 1) = #{deptNo}

mapper.update:
UPDATE employee
SET dept_no   = #{dept_no},
    title_no  = #{title_no},
    emp_level = #{emp_level}
WHERE emp_no = #{emp_no};

UPDATE vacation
SET vac_left = #{vac_left},
    vac_add  = vac_add +#{difference}
WHERE emp_no = #{emp_no};

mapper.select:
SELECT va.vac_no, va.vac_apply_date, va.vac_type, va.vac_apply_status, va.vac_apply_status_final, e.emp_name, va.vac_reject_reason, va.vac_reject_reason_final FROM vacation_apply va JOIN employee e ON va.emp_no = e.emp_no JOIN department d ON e.dept_no = d.dept_no
va.vac_apply_status = true
AND va.vac_apply_date = #{filterVacDate}
AND va.vac_apply_status_final = true
AND va.vac_apply_status_final = false
ORDER BY va.vac_no DESC LIMIT #{pageSize} OFFSET #{page}

mapper.update:
UPDATE vacation_apply SET 
vac_apply_status_final = true
WHERE vac_no = #{vac_no}

  	UPDATE vacation_apply SET 
	 	vac_reject_reason_final = #{vac_reject_reason}
	 	WHERE vac_no = #{vac_no}

UPDATE vacation
SET vac_left = vac_left - #{use}
WHERE emp_no = #{emp_no}

mapper.select:
SELECT e.emp_no, e.emp_name, t.title_name, d.dept_name, e.emp_employment_status, s.sal_total, CASE WHEN s.sal_emp_no IS NOT NULL THEN TRUE ELSE FALSE END AS salwrite FROM employee e LEFT JOIN title t ON e.title_no = t.title_no LEFT JOIN department d ON e.dept_no = d.dept_no LEFT JOIN salary s ON e.emp_no = s.sal_emp_no
AND DATE_FORMAT(s.sal_register_date, '%Y-%m') = #{filterSalMonth}
e.emp_employment_status = 1
AND e.emp_no LIKE CONCAT('%', #{searchText}, '%')
AND e.emp_name LIKE CONCAT('%', #{searchText}, '%')
AND t.title_name LIKE CONCAT('%', #{searchText}, '%')
AND d.dept_name LIKE CONCAT('%', #{searchText}, '%')
AND (CASE WHEN s.sal_emp_no IS NOT NULL THEN TRUE ELSE FALSE END) = #{filterSalResult}
LIMIT #{pageSize} OFFSET #{page}
