mapper.select:
SELECT pro_who, pro_cash, pro_actual_date, pro_date, pro_category, pro_content, pro_cash FROM profit
AND pro_category = #{category}
AND pro_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
AND pro_who LIKE CONCAT('%', #{searchText}, '%')
ORDER BY pro_cash DESC
ORDER BY pro_cash ASC
ORDER BY pro_who ASC
ORDER BY pro_actual_date DESC
LIMIT #{pageSize} OFFSET #{page}

SELECT COUNT(pro_idx)
FROM profit


		AND pro_category = #{category}


		AND pro_actual_date BETWEEN #{filterStartDate} AND
		#{filterEndDate}


		AND pro_who LIKE CONCAT('%', #{searchText}, '%')

SELECT exp_sal_idx, exp_driver_salary_idx, exp_category, exp_date, exp_content, exp_who, exp_cash,exp_actual_date FROM expenses
AND exp_category = #{category}
AND exp_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
AND exp_who LIKE CONCAT('%', #{searchText}, '%')
ORDER BY exp_cash DESC
ORDER BY exp_cash ASC
ORDER BY exp_who ASC
ORDER BY exp_actual_date DESC
LIMIT #{pageSize} OFFSET #{page}

SELECT COUNT(exp_idx)
FROM expenses


		AND exp_category = #{category}


		AND exp_actual_date BETWEEN #{filterStartDate} AND
		#{filterEndDate}


		AND exp_who LIKE CONCAT('%', #{searchText}, '%')

SELECT pro.pro_actual_date AS deal_actual_date, pro.pro_date AS deal_date, '수익' AS deal_category, pro.pro_category AS deal_field, pro.pro_content AS deal_content, pro.pro_who AS deal_who, pro.pro_cash AS deal_cash, dh.deal_idx, dh.deal_emp_no, dh.deal_pro_idx, dh.deal_exp_idx FROM profit pro JOIN deal_history dh ON dh.deal_pro_idx = pro.pro_idx
AND '수익' IN
#{cat}
AND pro.pro_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
AND pro.pro_who LIKE CONCAT('%', #{searchText}, '%')
UNION SELECT exp.exp_actual_date AS deal_actual_date, exp.exp_date AS deal_date, '지출' AS deal_category, exp.exp_category AS deal_field, exp.exp_content AS deal_content, exp.exp_who AS deal_who, exp.exp_cash AS deal_cash, dh.deal_idx, dh.deal_emp_no, dh.deal_pro_idx, dh.deal_exp_idx FROM expenses exp JOIN deal_history dh ON dh.deal_exp_idx = exp.exp_idx
AND '지출' IN
#{cat}
AND exp.exp_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
AND exp.exp_who LIKE CONCAT('%', #{searchText}, '%')
ORDER BY deal_cash DESC
ORDER BY deal_cash ASC
ORDER BY deal_who ASC
ORDER BY deal_actual_date DESC
LIMIT #{pageSize} OFFSET #{page}

SELECT COUNT(deal_idx) FROM ( SELECT pro.pro_idx AS deal_idx FROM profit pro JOIN deal_history dh ON dh.deal_pro_idx = pro.pro_idx
AND pro.pro_category IN
#{cat}
AND pro.pro_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
AND pro.pro_who LIKE CONCAT('%', #{searchText}, '%')
UNION SELECT exp.exp_idx AS deal_idx FROM expenses exp JOIN deal_history dh ON dh.deal_exp_idx = exp.exp_idx
AND exp.exp_category IN
#{cat}
AND exp.exp_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
AND exp.exp_who LIKE CONCAT('%', #{searchText}, '%')
) AS deal_history

SELECT IFNULL(SUM(profit_cash), 0) AS total_profit, IFNULL(SUM(expense_cash), 0) AS total_expense, (IFNULL(SUM(profit_cash), 0) - IFNULL(SUM(expense_cash), 0)) AS net_profit FROM ( SELECT pro.pro_cash AS profit_cash, 0 AS expense_cash FROM profit pro JOIN deal_history dh ON pro.pro_idx = dh.deal_pro_idx
AND pro.pro_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
UNION ALL SELECT 0 AS profit_cash, exp.exp_cash AS expense_cash FROM expenses exp JOIN deal_history dh ON exp.exp_idx = dh.deal_exp_idx
AND exp.exp_actual_date BETWEEN #{filterStartDate} AND #{filterEndDate}
) AS deal_amounts
