mapper.select:
SELECT emp_password
FROM employee
WHERE emp_no = #{emp_no}

SELECT profile_new
FROM profile_photo
WHERE emp_no = #{emp_no}

mapper.insert:
INSERT INTO vacation_apply ( emp_no, vac_use, vac_type, vac_reason, vac_use_date
, vac_use_end_date
) VALUES ( #{emp_no}, #{vac_use}, #{vac_type}, #{vac_reason}, #{vac_use_date}
, #{vac_use_end_date}
)

mapper.select:
SELECT va.vac_no, va.vac_apply_date, va.vac_type, va.vac_apply_status, va.vac_apply_status_final
FROM vacation_apply va

	va.emp_no = #{emp_no}

    AND va.vac_apply_date = #{filterVacDate}




            AND va.vac_apply_status_final = true


            AND va.vac_apply_status_final = false




LIMIT #{pageSize} OFFSET #{page}

SELECT COUNT(va.vac_no)
FROM vacation_apply va

	va.emp_no = #{emp_no}

    AND va.vac_apply_date = #{filterVacDate}




            AND va.vac_apply_status_final = true


            AND va.vac_apply_status_final = false
