mapper.select:
SELECT notice_idx, notice_writer, notice_field, notice_imp, notice_stat, notice_title, notice_date FROM notice WHERE notice_stat != '비활성화'
AND (notice_field = 'all' OR notice_field = #{category})
AND notice_date >= #{filterStartDate}
AND notice_date <= #{filterEndDate}
AND notice_writer LIKE CONCAT('%', #{searchQuery}, '%')
AND notice_title LIKE CONCAT('%', #{searchQuery}, '%')
ORDER BY notice_imp DESC, notice_date DESC LIMIT #{pageSize} OFFSET #{page}

SELECT notice_file_name 
FROM notice_attach_file 
WHERE notice_idx = #{notice_idx}
